import React from "react";
import styled from "styled-components";

const TimerWrapper = styled.h1`
  position: relative;
  width: 300px;
  color: #f05550;
`;

const start = num => {
  this.setState({seconds: num})
}

const minutes = seconds => (seconds / 60).toFixed() + ' : ' + seconds % 60 ;

class Timer extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      seconds: 1500
    };
  }

  render() {
    return (
      <TimerWrapper onClick={start(this.state.seconds-1)}>
        TOMATO 25:00 {minutes(this.state.seconds)}
      </TimerWrapper>
    );
  }
};
export default Timer;
