import React from "react";
import styled from "styled-components";

const TimerWrapper = styled.h1`
  position: relative;
  width: 300px;
  color: #f05550;
`;

const minutes = seconds => (seconds / 60).toFixed() + ' : ' + seconds % 60 ;

class Timer extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      seconds: 1500
    };
  }

  start = num => {

    setInterval(num=>console.log(num),1000)

    this.setState({seconds: num})
    console.log(this.state.seconds)
  }

  render() {
    return (
      <TimerWrapper>
        TOMATO 25:00 {minutes(this.state.seconds)}{this.start}
      </TimerWrapper>
    );
  }
};
export default Timer;
