import React from "react";
import styled from "styled-components";

const TimerWrapper = styled.h1`
  position: relative;
  width: 300px;
  color: #f05550;
`;

const minutes = seconds => (seconds/60).toFixed(0)+' : '+seconds%60;

class Timer extends React.PureComponent {
  constructor(props){
    super(props);
    this.state={
        seconds: 1359
      };
  }
  
  render(){
  return (
    <TimerWrapper>
        TOMATO 25:00 {minutes(this.state.seconds)}
    </TimerWrapper>
  );
  }
};
export default Timer;
